function tb=tableTest() 	x=(1:5)';	y=x.^2;	z=['first';'second';'third';'fourth';'fifth/5th'];	t=table(x,y,z)	tb=[t.Properties.VariableNames;t.data];	disp(table([x,y],z))	disp(table(x,y,z,x,y,z,x,y,z,x,y,z))	disp(table(x,y,z,'VariableNames',{'x','y','z'}))	disp(table([x,y],z,'VariableNames',{'p','z'}))		try		disp(['Should err: all variables should have the same number of rows'])		table(x,y,[z;'extra line']);	catch ex		disp(['Happened:', ex.message])	end		try		disp(['Should err: "VariableNames" is not followed by the appropriate column headings'])		table(x,y,z,'VariableNames');	catch ex		disp(['Happened:', ex.message])	end		try		disp(['Should err: "VariableNames" is not followed by the appropriate column headings'])		table(x,y,z,'VariableNames',{'X Column','Y Column'});	catch ex		disp(['Happened:', ex.message])	end		try		disp(['Should err: "VariableNames" is not followed by the appropriate column headings'])		table(x,y,z,'VariableNames',x);	catch ex		disp(['Happened:', ex.message])	end		try		disp(['Should err: "VariableNames" is not followed by the appropriate column headings'])		table(x,y,z,'VariableNames',{'X','Y','Z','Extra column'});	catch ex		disp(['Happened:', ex.message])	end	end